	//1.定义圆形覆盖物的构造函数  
    function ComplexCustomOverlay(point, text, text1, index, type){
      this._point = point;
      this._text = text;
	  this._text1 = text1;
	  this._index = index;
	  this._type = type;
    }
	// 继承API的BMap.Overlay  
    ComplexCustomOverlay.prototype = new BMap.Overlay();
	//初始化自定义圆形覆盖物
    ComplexCustomOverlay.prototype.initialize = function(map){
		// 保存map对象实例  
      this._map = map;
	  // 创建div元素，作为自定义覆盖物的容器  
      var div = this._div = document.createElement("div");
      div.style.position = "absolute";
      div.style.zIndex = BMap.Overlay.getZIndex(this._point.lat);
      div.style.backgroundColor = "#BC3B3A";
      div.style.borderRadius = "50%";
      div.style.color = "white";
      div.style.height = "90px";
	  div.style.width = "90px";
	  //div.style.lineHeight = "90px";
	  div.style.textAlign = "center";
      div.style.padding = "2px";
      div.style.whiteSpace = "nowrap";
      div.style.MozUserSelect = "none";
      div.style.fontSize = "14px";
	  var span = this._span = document.createElement("div");
	  var span1 = this._span = document.createElement("div");
	  span.style.marginTop = "25px";
      div.appendChild(span);	  
	  div.appendChild(span1);
      span.appendChild(document.createTextNode(this._text)); 
	  span1.appendChild(document.createTextNode(this._text1 + "套"));
	  var that = this;
	  if(this._type == "1") {	//行政区
		div.onclick = function(e){
			  getHouse();
			  mp.setCenter("深圳市" + that._text);
			  mp.setZoom(14);
		  }	
		  div.onmouseover = function(){
			polygon1[that._index].show();
		  }
		  div.onmouseout = function(){
			polygon1[that._index].hide();
		  }  
	  }else{
		 div.onclick = function(e){
			  getHouse();
			  mp.setCenter(point);
			  mp.setZoom(16);
		  }	
		  div.onmouseover = function(){
			polygon2[that._index].show();
		  }
		  div.onmouseout = function(){
			polygon2[that._index].hide();
		  } 
	  }
      mp.getPanes().labelPane.appendChild(div);
      // 需要将div元素作为方法的返回值，当调用该覆盖物的show, hide方法，或者对覆盖物进行移除时，API都将操作此元素。
      return div;
    }
	//绘制圆形覆盖物
    ComplexCustomOverlay.prototype.draw = function(){
      var map = this._map;
	  // 根据地理坐标转换为像素坐标，并设置给容器 
      var pixel = map.pointToOverlayPixel(this._point);
      this._div.style.left = pixel.x - 50 + "px";
      this._div.style.top  = pixel.y - 30 + "px";
    }
	// 2.自定义小区矩形覆盖物
    function ComplexCustomOverlay1(point, text, mouseoverText){
      this._point = point;
      this._text = text;
      this._overText = mouseoverText;
    }
    ComplexCustomOverlay1.prototype = new BMap.Overlay();
    ComplexCustomOverlay1.prototype.initialize = function(map){
      this._map = map;
      var div = this._div = document.createElement("div");
      div.style.position = "absolute";
      div.style.zIndex = BMap.Overlay.getZIndex(this._point.lat);
      div.style.backgroundColor = "#EE5D5B";
      div.style.border = "1px solid #BC3B3A";
      div.style.color = "white";
      div.style.height = "24px";
      div.style.padding = "2px";
      div.style.lineHeight = "18px";
      div.style.whiteSpace = "nowrap";
      div.style.MozUserSelect = "none";
      div.style.fontSize = "12px"
      var span = this._span = document.createElement("span");
      div.appendChild(span);
      span.appendChild(document.createTextNode(this._text));      
      var that = this;
      var arrow = this._arrow = document.createElement("div");
      arrow.style.background = "url(http://map.baidu.com/fwmap/upload/r/map/fwmap/static/house/images/label.png) no-repeat";
      arrow.style.position = "absolute";
      arrow.style.width = "11px";
      arrow.style.height = "10px";
      arrow.style.top = "22px";
      arrow.style.left = "10px";
      arrow.style.overflow = "hidden";
      div.appendChild(arrow);
      div.onmouseover = function(){
        this.style.backgroundColor = "#6BADCA";
        this.style.borderColor = "#0000ff";
        this.getElementsByTagName("span")[0].innerHTML = that._text + that._overText + "套";
        arrow.style.backgroundPosition = "0px -20px";
      }
      div.onmouseout = function(){
        this.style.backgroundColor = "#EE5D5B";
        this.style.borderColor = "#BC3B3A";
        this.getElementsByTagName("span")[0].innerHTML = that._text;
        arrow.style.backgroundPosition = "0px 0px";
      }
      mp.getPanes().labelPane.appendChild(div);
      return div;
    }
    ComplexCustomOverlay1.prototype.draw = function(){
      var map = this._map;
      var pixel = map.pointToOverlayPixel(this._point);
      this._div.style.left = pixel.x - parseInt(this._arrow.style.left) + "px";
      this._div.style.top  = pixel.y - 30 + "px";
    }
